import flaskfrom flask import request, jsonifyapp = flask.Flask(__name__)app.config["DEBUG"] = True# Create some test data for our catalog in the form of a list of dictionaries.books = [    {'race number': 'Race 3',     'race type': 'pokemon',     'race name': 'Pikachu',     'venue': 'Central Park',     'start time': '13.49'},    {'race number': 'Race 6',     'race type': 'pokemon',     'race name': 'Mew',     'venue': 'University of Sydney',     'start time': '13.51'},    {'race number': 'Race 4',     'race type': 'Human',     'race name': 'Ash Ketchum',     'venue': 'UNSW',     'start time': '13.56'},     {'race number': 'Race X',     'race type': 'Student',     'race name': 'Chris Jin',     'venue': 'Newtown',     'start time': '14.05'},]@app.route('/', methods=['GET'])def home():    return '''<h1 style="background-color:powderblue;">Next to Jump</h1>'''# A route to return all of the available entries in our catalog.@app.route('/api/v1/information/all', methods=['GET'])def api_all():    return jsonify(books)app.run()