import flaskfrom flask import request, jsonifyapp = flask.Flask(__name__)app.config["DEBUG"] = Trueraces = [    {'race number': 0,     'race type': 'pokemon',     'race name': 'Pikachu',     'venue': 'Central Park',     'start time': '13.49'},    {'race number': 1,     'race type': 'pokemon',     'race name': 'Mew',     'venue': 'University of Sydney',     'start time': '13.51'},    {'race number': 2,     'race type': 'Human',     'race name': 'Ash Ketchum',     'venue': 'UNSW',     'start time': '13.56'},     {'race number': 3,     'race type': 'Student',     'race name': 'Chris Jin',     'venue': 'Newtown',     'start time': '14.05'},]@app.route('/', methods=['GET'])def home():    return '''<h1 style="background-color:powderblue;">Next to Jump</h1>'''    @app.route('/api/v1/information/races/all', methods=['GET'])def api_all():    return jsonify(races)@app.route('/api/v1/information/races', methods=['GET'])def api_id():    if 'race number' in request.args:        id = int(request.args['race number'])    else:        return "Error: No race number field provided. Please specify a race number."    results = []    for race in races:        if race['race number'] == id:            results.append(race)    return jsonify(results)app.run()